// 2x1 Multiplexor using logic-gates

						module glitchEx(

						);

						wire not_sel;
						wire and_out1, and_out2;

						assign not_sel = ~sel;
						assign and_out1 = not_sel & in0;
						assign and_out2 = sel & in1;

						// Buggy line of code:
						assign z = and_out1 | and_out2; // glitch in signal z

						endmodule
